
freq = "ETAOINSHRDLU".lower()
dict = {}
for x in "abcdefghijklmnopqrstuvwxyz":
    dict[x] = 0
sample = open("study-guide.txt", "r")
fin = "{G6D4U_4G41T515_15_73Y10A5_42JX1770}"
for x in fin:
    if x in dict:
        dict[x] += 1
char_freq = []
for x, y in zip(dict.keys(), dict.values()):
    char_freq += [[x, y]]
char_freq = sorted(char_freq, key = lambda x: x[1])
char_freq = char_freq[::-1]
print(char_freq)
correlate = {}
##https://crypto.interactive-maths.com/frequency-analysis-breaking-the-code.html
for x, y in zip(char_freq[0:len(freq)], freq):
    correlate[x[0]] = y
decrypt = fin
out = [x for x in (decrypt.replace("ra", 'ER').replace("wt", "TH"))]
for x in range(len(out)):
    out[x] = correlate[out[x]] if out[x] in correlate else out[x]
out = "".join(x for x in out)
print(out)

